export default function slideToClosest(s=this.params.speed,a=!0,r,e=.5){const t=this;let i=t.activeIndex;const n=Math.min(t.params.slidesPerGroupSkip,i),l=n+Math.floor((i-n)/t.params.slidesPerGroup),p=t.rtlTranslate?t.translate:-t.translate;if(p>=t.snapGrid[l]){const s=t.snapGrid[l];p-s>(t.snapGrid[l+1]-s)*e&&(i+=t.params.slidesPerGroup)}else{const s=t.snapGrid[l-1];p-s<=(t.snapGrid[l]-s)*e&&(i-=t.params.slidesPerGroup)}return i=Math.max(i,0),i=Math.min(i,t.slidesGrid.length-1),t.slideTo(i,s,a,r)}